CREATE table camping_object(
	camping_name varchar2(150) PRIMARY key,
	camping_price number,
	camping_reg_date DATE,
	camping_image varchar2(150),
	camping_image_detail varchar2(150)
);


CREATE TABLE camping_object_cart(
    NO number primary key,
    user_id varchar2(20),
    camping_object_name varchar2(150),
    camping_object_image varchar2(150),
    camping_object_price number,
    camping_object_tot_price number
);

create sequence seq_camping_object_cart
start with 1
increment by 1;

