mapper.insert:
 INSERT INTO CAMPING_BOARD_LIST(    BNO 
					       , TITLE 
					       , CONTENT 
					       , WRITER  
					       , PASSWORD )
               VALUES(    CAMPING_BOARD_LIST_SEQ.NEXTVAL 
              		   , #{title}
              		   , #{content}
              		   , #{writer} 
              		   , #{password} )

mapper.update:
UPDATE CAMPING_BOARD_LIST
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}

mapper.delete:
DELETE 
  FROM CAMPING_BOARD_LIST
 WHERE BNO = #{bno}  
   AND PASSWORD = #{password}

mapper.update:
UPDATE CAMPING_BOARD_LIST SET
HIT = HIT+1
WHERE BNO = #{bno}
